//! getStaticProps => static generation(fetch data at build)
export async function getStaticProps() {
  //! consider an addition for loading and error

  // ! michigan current state date
  const res = await fetch(
    "https://api.covidtracking.com/v1/states/mi/current.json"
  );
  const data = await res.json();

  //! pass the data to the page as props
  return {
    props: { data, },
  };
}

function miStatsDisplay({ data, secondData }) { 
  return (
    <>
    <section>
      <h1> Current Michigan Data</h1>
      <h2>Data displayed was last updated: {data.date.toString()}</h2>
      <h2>Currently hospitalized: {data.hospitalizedCurrently} </h2>
      <h2>Currently in ICU: {data.inIcuCurrently}</h2>
  <h2>Deaths: {data.deaths}</h2>
    </section>
  </>
  );
}

export default statsDisplay;
