async function fetchData() {


const urls = [
  "https://api.covidtracking.com/v1/states/mi/current.json",
  "https://api.covidtracking.com/v1/us/current.json"
]

urls.forEach((_, idx) => {
  let res = fetch(`${urls[idx]}`)
  let data = res.json();
  return data;
})

}

fetchData();

export default function Index(miData, usData) {
  let [miStats, usStats] = [miData,usData];


  return (
    <>
      <section>
      <h1> Current Michigan Data</h1>
          <h2>
            {/* Data displayed was last updated: {miStats.date.toString()} */}
          </h2>
          <h2>Currently hospitalized: {miStats.hospitalizedCurrently} </h2>
          <h2>Currently in ICU: {miStats.inIcuCurrently}</h2>
          <h2>Deaths: {miStats.deaths}</h2>
      </section>
    </>
  )

  }
