//! getStaticProps => static generation(fetch data at build)
async function getStaticProps() {
  //! consider an addition for loading and error

  Promise.all([
    fetch("https://api.covidtracking.com/v1/states/mi/current.json"),
    fetch("https://api.covidtracking.com/v1/us/current.json"),
  ]).then(([miStats, usStats]) => {
    render(

        <>
        <section>
          <h1> Current Michigan Data</h1>
          <h2>
            Data displayed was last updated: {miStats.date.toString()}
          </h2>
          <h2>Currently hospitalized: {miStats.hospitalizedCurrently} </h2>
          <h2>Currently in ICU: {miStats.inIcuCurrently}</h2>
          <h2>Deaths: {miStats.deaths}</h2>
        </section>
      </>

    )
  });

  //! pass the data to the page as props
  return {
    props: { miDisplayStats, usDisplayStats },
  };
}

function Index({ miDisplayStats, usDisplayStats }) {
  return (
    <>
      <section>
        <h1> Current Michigan Data</h1>
        <h2>
          Data displayed was last updated: {miDisplayStats.date.toString()}
        </h2>
        <h2>Currently hospitalized: {miDisplayStats.hospitalizedCurrently} </h2>
        <h2>Currently in ICU: {miDisplayStats.inIcuCurrently}</h2>
        <h2>Deaths: {miDisplayStats.deaths}</h2>
      </section>
    </>
  );
}

export default Index;
