import getData from "./getData";

export async function getStaticProps(context) {
  const miStats = await getData(
    "https://api.covidtracking.com/v1/states/mi/current.json"
  );
  const usStats = await getData(
    "https://api.covidtracking.com/v1/us/current.json"
  );

  return {
    props: {
      miStats,
      usStats,
    },
  };
}

function Index(props) {

  return (
    <>
      <section className="bg-gray-900 p-6 rounded">
        <h1 className="text-blue-700"> Current Michigan Data</h1>
        <h2 className="my-1 text-gray-400">Data Quality Grade: {props.miStats.dataQualityGrade}</h2>
        <h2 className="my-1 text-gray-400">Data last updated: {props.miStats.date}</h2>
        <h2 className="my-1 text-gray-400">Currently hospitalized: <span className="text-orange-400"> {props.miStats.hospitalizedCurrently} </span></h2>
        <h2 className="my-1 text-gray-400">Currently in ICU: <span class=" text-indigo-400 text-right">{props.miStats.inIcuCurrently}</span></h2>
        <h2 className="my-1 text-gray-400">Deaths: <span class=" text-purple-400 text-right">{props.miStats.death}</span></h2>
        <h2 className="my-1 text-gray-400">Recovered: <span className></span>{props.miStats.recovered}</h2>
      </section>
      <section>
        <h1> Current US Data</h1>
        {/* <h2>Data last updated: {props.usStats[0].death}</h2> */}
        <h2>Currently hospitalized: {props.usStats[0].hospitalizedCurrently} </h2>
        <h2>Currently in ICU: {props.usStats[0].inIcuCurrently}</h2>
        <h2>Deaths: {props.usStats[0].death}</h2>
        <h2>Recovered: {props.usStats[0].recovered}</h2>
      </section>
    </>
  );
}

export default Index;
