import getData from "./getData";
import MiDisplay from "../components/miDisplay"

export async function getStaticProps(context) {
  const miStats = await getData(
    "https://api.covidtracking.com/v1/states/mi/current.json"
  );
  const usStats = await getData(
    "https://api.covidtracking.com/v1/us/current.json"
  );
  const miInfo = await getData(
    "https://api.covidtracking.com/v1/states/mi/info.json"
  );

  return {
    props: {
      miStats,
      usStats,
      miInfo,
    },
  };
}

function Index(props) {
  return (
    <>
      <section className="bg-gray-900 p-6 rounded m-1">
        <h1 className="text-blue-700 text-3xl"> Current Michigan Data </h1>
        <h2 className="my-1 text-gray-400 pl-4">
          Data Quality Grade: {props.miStats.dataQualityGrade}
        </h2>
        <h2 className="my-1 text-gray-400 pl-4">
          Data last updated: {props.miStats.date}{" "}
          <span className="text-gray-700 pl-2"> YYYYMMDD </span>
        </h2>
        <h2 className="my-1 text-gray-400 pl-4">
          Currently hospitalized:{" "}
          <span className="text-orange-400 pl-2">
            {" "}
            {props.miStats.hospitalizedCurrently}{" "}
          </span>
        </h2>
        <h2 className="my-1 text-gray-400 pl-4">
          Currently in ICU:{" "}
          <span className="text-indigo-400 pl-2">
            {props.miStats.inIcuCurrently}
          </span>
        </h2>
        <h2 className="my-1 text-gray-400 pl-4">
          Deaths:{" "}
          <span className=" text-purple-400 pl-2">{props.miStats.death}</span>
        </h2>
        <h2 className="my-1 text-gray-400 pl-4">
          Recovered:{" "}
          <span className="text-green-400 pl-2">{props.miStats.recovered}</span>
        </h2>

        <h2 className="text-3xl my-4 text-blue-700">State Links</h2>

        <a
          className="miCardLink"
          href={props.miInfo.covid19Site}
          target="_blank"
          rel="noreferrer noopener duration-700 "
        >
          Michigan state website
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              stroke-width="2"
              d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14"
            />
          </svg>
        </a>
        <a
          className="miCardLink"
          href={props.miInfo.twitter}
          target="_blank"
          rel="noreferrer noopener duration-700 "
        >
          Michigan state twitter
          {/* at-symbol svg heroicon */}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              stroke-width="2"
              d="M16 12a4 4 0 10-8 0 4 4 0 008 0zm0 0v1.5a2.5 2.5 0 005 0V12a9 9 0 10-9 9m4.5-1.206a8.959 8.959 0 01-4.5 1.207"
            />
          </svg>

          {/* external-link svg heroicon */}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              stroke-width="2"
              d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14"
            />
          </svg>
        </a>
      </section>

      <section className="bg-gray-900 p-6 rounded m-1">
        <h1 className="text-red-700"> Current US Data</h1>
        {/* <h2>Data last updated: {props.usStats[0].death}</h2> */}
        <h2 className="my-1 text-gray-400 pl-4">
          Currently hospitalized:{" "}
          <span className="text-orange-400 pl-2">
            {props.usStats[0].hospitalizedCurrently}{" "}
          </span>
        </h2>
        <h2 className="my-1 text-gray-400 pl-4">
          Currently in ICU:{" "}
          <span className="text-indigo-400 pl-2">
            {props.usStats[0].inIcuCurrently}
          </span>
        </h2>
        <h2 className="my-1 text-gray-400 pl-4">
          Deaths:{" "}
          <span className=" text-purple-400 pl-2">
            {props.usStats[0].death}
          </span>
        </h2>
        <h2 className="my-1 text-gray-400 pl-4">
          Recovered:{" "}
          <span className="text-green-400 pl-2">
            {props.usStats[0].recovered}
          </span>
        </h2>
      </section>
    </>
  );
}

export default Index;
